    private void computeButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_computeButtonActionPerformed
        this.outputField.setText(""+calc.calculate(this.inputField.getText()));
        if (this.outputField.getText().equals("-12321.12321")) {
            this.outputField.setText("Invalid Input");
        }
        this.inputField.requestFocus();
        this.inputField.setCaretPosition(caretPos);
    }//GEN-LAST:event_computeButtonActionPerformed

    private void inputFieldKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_inputFieldKeyPressed
        if (evt.getKeyCode()== KeyEvent.VK_ENTER) {
            this.computeButton.doClick();
        }
    }//GEN-LAST:event_inputFieldKeyPressed

