    private void leftButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_leftButtonActionPerformed
        int pos = caretPos;
        this.inputField.requestFocus();
        this.inputField.setCaretPosition(Math.max(pos - 1,0));
    }//GEN-LAST:event_leftButtonActionPerformed

    private void rightButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_rightButtonActionPerformed
        int pos = caretPos;
        this.inputField.requestFocus();
        this.inputField.setCaretPosition(Math.min(pos + 1,this.inputField.getText().length()));
    }//GEN-LAST:event_rightButtonActionPerformed

    private void clearButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_clearButtonActionPerformed
        this.inputField.setText("");
        caretPos = 0;
        this.inputField.requestFocus();
    }//GEN-LAST:event_clearButtonActionPerformed

    private void deleteButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_deleteButtonActionPerformed
        int pos = caretPos;
        if (pos != 0) {
            String beforeText = this.inputField.getText().substring(0, Math.max(caretPos-1,0));
            String afterText = this.inputField.getText().substring(caretPos);
        
            this.inputField.setText(beforeText + afterText);
        }
        this.inputField.requestFocus();
        this.inputField.setCaretPosition(Math.max(pos-1,0));
    }//GEN-LAST:event_deleteButtonActionPerformed

    private void openBracketButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_openBracketButtonActionPerformed
        insertTextWithButton("(", 1);
    }//GEN-LAST:event_openBracketButtonActionPerformed

    private void closeBracketButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_closeBracketButtonActionPerformed
        insertTextWithButton(")", 1);
    }//GEN-LAST:event_closeBracketButtonActionPerformed

    private void insertTextWithButton(String string,int caretPositionOffset) {
        int pos = caretPos;
        String beforeText = this.inputField.getText().substring(0, caretPos);
        String afterText = this.inputField.getText().substring(caretPos);
        
        this.inputField.setText(beforeText + string + afterText);
        
        this.inputField.requestFocus();
        this.inputField.setCaretPosition(pos + caretPositionOffset);
    }
    
